mapper.update:
update teacher
tname=#{tname},gender=#{gender},age=#{age},directionId=#{directionId},positionId=#{directionId},profession=#{profession}, school=#{school},diploma=#{diploma},experience=#{experience},tel=#{tel},email=#{email},state=#{state},
graduateDate=#{graduateDate},
hiredate=#{hireDate},
leavereason=#{leaveReason}
tid=#{tid}

UPDATE xueji SET
imgProof=#{arg1}
WHERE id=#{arg0}

update.foreach:
update chapter

    cpname=#{chapter.cpname},
    cpdescribe=#{chapter.cpdescribe},
    period=#{chapter.period}

where cpid = #{chapter.cpid}

mapper.select:
select * from teacher t, positions p, direction d
where t.positionId=p.pid and t.directionId=d.did AND hiredate BETWEEN #{arg0} AND #{arg1}
AND t.positionId=#{arg2}
AND t.tid=#{arg3}

select * from classteacher ct,center c
where ct.centerId=c.cid AND hireDate BETWEEN #{arg0} AND #{arg1}
 AND centerId=#{arg2}
 AND ctid=#{arg3}

select xj.classcode k, count(*) v
from xueji xj
 INNER JOIN class_baseinfo cb
WHERE typee='休学复学'
AND timee BETWEEN #{startTime} AND #{endTime}
AND cb.classcode=cb.classcode
AND cb.centerId=#{centerId}
AND cb.classteacherId=#{ctid}
AND xj.classcode=#{classcode}
GROUP BY xj.classcode

select xj.targetClasscode k,count(*) v
from xueji xj
 INNER JOIN class_baseinfo cb
WHERE xj.typee='重修'
AND xj.timee BETWEEN #{startTime} AND #{endTime}
AND cb.classcode=cb.classcode
AND cb.centerId=#{centerId}
AND cb.classteacherId=#{ctid}
AND xj.classcode=#{classcode}
GROUP BY xj.targetClasscode

