select xj.classcode k,count(*) v
from xueji xj
 INNER JOIN class_baseinfo cb
WHERE xj.typee='退学'
AND xj.timee BETWEEN #{startTime} AND #{endTime}
AND cb.classcode=cb.classcode
AND cb.centerId=#{centerId}
AND cb.classteacherId=#{ctid}
AND xj.classcode=#{classcode}
GROUP BY xj.classcode

select xj.classcode k, count(*) v
from xueji xj INNER JOIN class_baseinfo cb
WHERE xj.typee='过程中自主择业-理论课未结束'
AND xj.classcode=cb.classcode
AND xj.timee BETWEEN #{startTime} AND #{endTime}
AND (
((xj.classcode LIKE '%q%') AND (DATEDIFF(xj.timee,cb.beginDate) < 90)) OR
((xj.classcode LIKE '%z%') AND (DATEDIFF(xj.timee,cb.beginDate) < 135))
)
AND cb.centerId=#{centerId}
AND cb.classteacherId=#{ctid}
AND xj.classcode=#{classcode}
GROUP BY xj.classcode

SELECT xj.classcode k, count(*) v
FROM xueji xj INNER JOIN class_baseinfo cb
